// Monitor NCSC advisories - FREE!
const crypto = require('crypto');
const fs = require('fs');
const Parser = require('rss-parser');

class NCSCAdvisoryMonitor {
  constructor() {
    this.parser = new Parser();
    this.feedUrl = 'https://www.ncsc.gov.uk/api/1/services/v1/all-rss-feed.xml';
    this.stateFile = 'ncsc-seen.json';
    this.seenItems = this.loadState();
  }

  loadState() {
    try {
      return new Set(JSON.parse(fs.readFileSync(this.stateFile, 'utf8')));
    } catch {
      return new Set();
    }
  }

  saveState() {
    fs.writeFileSync(
      this.stateFile,
      JSON.stringify([...this.seenItems])
    );
  }

  async checkForNew() {
    const feed = await this.parser.parseURL(this.feedUrl);
    const newItems = [];

    for (const item of feed.items) {
      const hash = crypto
        .createHash('md5')
        .update(item.link)
        .digest('hex');

      if (!this.seenItems.has(hash)) {
        newItems.push(item);
        this.seenItems.add(hash);
      }
    }

    if (newItems.length > 0) {
      this.saveState();
    }

    return newItems;
  }

  async startMonitoring(intervalMs = 3600000) {
    console.log('Monitoring NCSC for new advisories...');

    const check = async () => {
      const newItems = await this.checkForNew();

      if (newItems.length > 0) {
        console.log(`\n[${new Date().toISOString()}] New advisories:`);
        newItems.forEach(item => {
          console.log(`- ${item.title}`);
          console.log(`  ${item.link}`);
        });
      }
    };

    await check(); // Initial check
    setInterval(check, intervalMs);
  }
}

// Start monitoring
const monitor = new NCSCAdvisoryMonitor();
monitor.startMonitoring();

