// Fetch NCSC RSS feeds - COMPLETELY FREE!
const Parser = require('rss-parser');
const parser = new Parser();

const ncscFeeds = {
  all: 'https://www.ncsc.gov.uk/api/1/services/v1/all-rss-feed.xml',
  news: 'https://www.ncsc.gov.uk/api/1/services/v1/news-rss-feed.xml',
  reports: 'https://www.ncsc.gov.uk/api/1/services/v1/report-rss-feed.xml',
  guidance: 'https://www.ncsc.gov.uk/api/1/services/v1/guidance-rss-feed.xml',
  blogs: 'https://www.ncsc.gov.uk/api/1/services/v1/blog-rss-feed.xml'
};

const fetchAllFeeds = async () => {
  const results = {};

  for (const [name, url] of Object.entries(ncscFeeds)) {
    try {
      const feed = await parser.parseURL(url);
      results[name] = feed.items.slice(0, 5).map(item => ({
        title: item.title,
        link: item.link,
        pubDate: item.pubDate,
        content: item.contentSnippet
      }));
      console.log(`Fetched ${feed.items.length} items from ${name} feed`);
    } catch (error) {
      console.error(`Error fetching ${name}: ${error.message}`);
    }
  }

  return results;
};

// Usage
fetchAllFeeds().then(feeds => {
  console.log('\nLatest NCSC Guidance (FREE):');
  feeds.guidance?.forEach(item => {
    console.log(`- ${item.title}`);
  });
});

python.monitor_advisories:
import feedparser
import hashlib
import json
import time
from datetime import datetime

class NCSCMonitor:
    def __init__(self):
        self.feed_url = 'https://www.ncsc.gov.uk/api/1/services/v1/all-rss-feed.xml'
        self.seen_items = set()
        self.state_file = 'ncsc_monitor.json'
        self.load_state()

    def load_state(self):
        try:
            with open(self.state_file, 'r') as f:
                self.seen_items = set(json.load(f))
        except FileNotFoundError:
            pass

    def save_state(self):
        with open(self.state_file, 'w') as f:
            json.dump(list(self.seen_items), f)

    def check_new_items(self):
        """Check for new NCSC updates - FREE!"""
        feed = feedparser.parse(self.feed_url)
        new_items = []

        for entry in feed.entries:
            # Create unique ID for each entry
            entry_id = hashlib.md5(entry.link.encode()).hexdigest()

            if entry_id not in self.seen_items:
                new_items.append({
                    'title': entry.title,
                    'link': entry.link,
                    'published': entry.published,
                    'summary': getattr(entry, 'summary', '')[:200]
                })
                self.seen_items.add(entry_id)

        if new_items:
            self.save_state()

        return new_items

    def monitor(self, interval=3600):
        """Monitor for new advisories"""
        print(f"Monitoring NCSC feeds (checking every {interval}s)...")

        while True:
            new_items = self.check_new_items()

            if new_items:
                print(f"\n[{datetime.now()}] New NCSC updates:")
                for item in new_items:
                    print(f"- {item['title']}")
                    print(f"  {item['link']}")

            time.sleep(interval)

# Start monitoring
monitor = NCSCMonitor()
new = monitor.check_new_items()
print(f"Found {len(new)} new items")
