javascript.fetch_feed:
// Fetch NIST publications - COMPLETELY FREE!
const fetchNISTPublications = async () => {
  const url = 'https://csrc.nist.gov/csrc/media/feeds/publication-feed.json';

  const response = await fetch(url);
  const publications = await response.json();

  console.log('Latest NIST Publications (FREE!):');

  publications.slice(0, 10).forEach(pub => {
    console.log(`\n${pub.title}`);
    console.log(`  ${pub.series} ${pub.number}`);
    console.log(`  Published: ${pub.publicationDate}`);
    console.log(`  URL: ${pub.url}`);
  });

  return publications;
};

fetchNISTPublications();

javascript.monitor_framework:
// Monitor Cybersecurity Framework updates - FREE!
const monitorCSF = async () => {
  const feedUrl = 'https://csrc.nist.gov/csrc/media/feeds/publication-feed.json';

  const response = await fetch(feedUrl);
  const pubs = await response.json();

  // Filter for CSF-related publications
  const csfPubs = pubs.filter(pub => 
    pub.title?.toLowerCase().includes('cybersecurity framework') ||
    pub.title?.toLowerCase().includes('csf')
  );

  if (csfPubs.length > 0) {
    console.log('Cybersecurity Framework Updates:');
    csfPubs.forEach(pub => {
      console.log(`- ${pub.title}`);
      console.log(`  ${pub.url}`);
    });
  }
};

monitorCSF();
