// Download paper PDF - FREE!
const fs = require('fs');
const https = require('https');

const downloadPaper = (year, paperId) => {
  const paddedId = String(paperId).padStart(3, '0');
  const url = `https://eprint.iacr.org/${year}/${paddedId}.pdf`;
  const filename = `${year}_${paddedId}.pdf`;

  const file = fs.createWriteStream(filename);

  https.get(url, (response) => {
    response.pipe(file);

    file.on('finish', () => {
      file.close();
      console.log(`Downloaded: ${filename}`);
    });
  }).on('error', (err) => {
    fs.unlink(filename, () => {});
    console.error(`Error downloading: ${err.message}`);
  });
};

// Download paper 2024/001
downloadPaper(2024, 1);

