// Fetch latest cryptography papers - COMPLETELY FREE!
const Parser = require('rss-parser');
const parser = new Parser();

const fetchLatestPapers = async () => {
  const feed = await parser.parseURL('https://eprint.iacr.org/rss/rss.xml');

  console.log(`Latest ${feed.items.length} papers (FREE!)`);

  feed.items.slice(0, 10).forEach(item => {
    console.log(`\n${item.title}`);
    console.log(`Link: ${item.link}`);
    console.log(`Published: ${item.pubDate}`);
  });

  return feed.items;
};

fetchLatestPapers();

javascript.parse_yearly:
// Get papers from specific year - FREE!
const axios = require('axios');
const cheerio = require('cheerio');

const getPapersByYear = async (year) => {
  const url = `https://eprint.iacr.org/complete/${year}`;
  const response = await axios.get(url);
  const $ = cheerio.load(response.data);

  const papers = [];

  $('tr').each((i, row) => {
    if (i === 0) return; // Skip header

    const cols = $(row).find('td');
    if (cols.length >= 3) {
      const paperLink = $(cols[0]).find('a');
      const paperId = paperLink.text().trim();
      const title = $(cols[1]).text().trim();
      const authors = $(cols[2]).text().trim();

      if (paperId) {
        papers.push({
          id: paperId,
          title,
          authors,
          url: `https://eprint.iacr.org/${paperId}`,
          pdf: `https://eprint.iacr.org/${paperId}.pdf`
        });
      }
    }
  });

  return papers;
};

// Get all 2024 papers
getPapersByYear(2024).then(papers => {
  console.log(`Found ${papers.length} papers in 2024`);
  papers.slice(0, 5).forEach(p => {
    console.log(`- ${p.title} by ${p.authors}`);
  });
});
